// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./EchoXInterfaces.sol";

interface IUniswapRouter {
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
}

contract EchoXAggregator is Ownable {
    address public pointsContract;
    mapping(string => address) public routers; // e.g., "arb" => 0xUniswapRouter

    event RouterSet(string chainKey, address router);
    event SwapExecuted(
        address indexed user,
        string chainKey,
        address[] path,
        uint256 amountIn,
        uint256 amountOut
    );

    constructor(address _pointsContract) {
        pointsContract = _pointsContract;
    }

    // Allow updating the Points contract (optional flexibility)
    function setPointsContract(address _pointsContract) external onlyOwner {
        pointsContract = _pointsContract;
    }

    // Set router for a specific chain (e.g., Arbitrum or Mumbai)
    function setRouter(string calldata key, address router) external onlyOwner {
        require(router != address(0), "Invalid router address");
        routers[key] = router;
        emit RouterSet(key, router);
    }

    // Core swap function
    function swap(
        string calldata chainKey,           // e.g., "arb"
        address[] calldata path,            // tokenIn -> tokenOut
        uint256 amountIn,                   // amount to swap
        uint256 amountOutMin                // min acceptable output
    ) external {
        address router = routers[chainKey];
        require(router != address(0), "Router not configured");

        IERC20(path[0]).transferFrom(msg.sender, address(this), amountIn);
        IERC20(path[0]).approve(router, amountIn);

        uint256[] memory amounts = IUniswapRouter(router).swapExactTokensForTokens(
            amountIn,
            amountOutMin,
            path,
            msg.sender,
            block.timestamp + 300
        );

        uint256 amountUsd = amounts[amounts.length - 1]; // Simplified USD value

        // Reward user for the swap
        try IEchoXPoints(pointsContract).recordDexAggregatorTrade(msg.sender, amountUsd) {
            // success
        } catch {
            // silently fail
        }

        // Record daily swap
        try IEchoXPoints(pointsContract).recordDailySwap(msg.sender) {
            // success
        } catch {
            // silently fail
        }

        emit SwapExecuted(msg.sender, chainKey, path, amountIn, amounts[amounts.length - 1]);
    }

    // View function to fetch the router for a chain
    function getRouter(string calldata chainKey) external view returns (address) {
        return routers[chainKey];
    }
}
